       //preloader
       setTimeout(function(){$("#loading").fadeOut(500);}, 1500);
       //mmenu
       $("#my-menu").mmenu({
           "extensions": [
               "effect-menu-slide",
               "pagedim-black",
               "fx-listitems-slide"
           ],
           "offCanvas": {
               "position": "right"
           },
           "navbar": {
               "title": "Содержание отчета"
           }
       });
       var api = $("#my-menu").data( "mmenu" );
       //   Hook into methods
       api.bind( "open:finish", function() {
           $(".hamburger").addClass('is-active');
       });
       api.bind( "close:finish", function( $panel ) {
           $(".hamburger").removeClass('is-active');
       });


       $(".mm-listview li a:not(.mm-next)").click(function() {      // When arrow is clicked
               api.close();
       });
     });

   $(document).on( "click", ".btn-down a", function() {
       if(!$(this).hasClass('is-active')){
           $(this).addClass('is-active');
           $($(this).data().slide).slideUp(500);

       }
       else{
           $(this).removeClass('is-active');
           $($(this).data().slide).slideDown(500);
       }
       Waypoint.refreshAll();//Обновляем waypoint

   });

   function onElementHeightChange(elm, callback){//Определяет изменение высоты элемента, использую для обновления waypoint
       var lastHeight = elm.clientHeight, newHeight;
       (function run(){
           newHeight = elm.clientHeight;
           if( lastHeight != newHeight )
               callback();
           lastHeight = newHeight;

           if( elm.onElementHeightChangeTimer )
               clearTimeout(elm.onElementHeightChangeTimer);

           elm.onElementHeightChangeTimer = setTimeout(run, 200);
       })();
   }


   onElementHeightChange(document.body, function(){
       Waypoint.refreshAll();//Обновляем waypoint
   });

   // ===== Scroll to Top ====
   $(window).scroll(function() {
       if ($(this).scrollTop() >= 50) {        // If page is scrolled more than 50px
           $('#return-to-top').fadeIn(200);    // Fade in the arrow
       } else {
           $('#return-to-top').fadeOut(200);   // Else fade out the arrow
       }
   });
   $('#return-to-top').click(function() {      // When arrow is clicked
       $('body,html').animate({
           scrollTop : 0                       // Scroll to top of body
       }, 500);
   });

   $('#my-menu a').click(function(){
        $("html, body").animate({ scrollTop: $($(this).attr('href')).offset().top - 20}, 600);
   });
</script>

